import { Routes, Route } from 'react-router-dom'
import { db } from './FireBase'

import './App.css';
import {
  getDocs,
  collection,
  query,
  onSnapshot,
} from "firebase/firestore";
import { Form, Teams, Images } from './component';


const URL = "http://localhost:7000/"
function App() {

  const [data, setData] = useState()
