  //     const responce = await axios.get(URL)
  //     setData(responce.data)
  //   }
  //   getData()
  // }, []);


console.log(data)
  return (
    <div>
<Routes>
  <Route path='/' element={<Form />}/>
  <Route path='/admin' element={<Teams data = {data}/>}/>
  <Route path='/admin/images/:teamId' element={<Images  data={data}/>}/>


</Routes>


    </div>
  );
}

export default App;
